mapper.select:
select c.course_id,c.course_name, c.academic_year, c.semester, u.username as teacherName
from course as c ,
     user as u ,
     student_course as sc
where c.course_id =sc.course_id
  and sc.student_id = #{studentId}
  and u.user_id=c.teacher_id
  and (c.course_name like concat(concat('%', #{keyword}), '%') or
       u.username like concat(concat('%', #{keyword}), '%'))
limit #{cur} ,10;

mapper.update:
UPDATE course
set course_name=#{courseName},
    course_description=#{course_description},
    semester=#{semester},
    academic_year=#{academic_year}
where course_id = #{courseId}
